let princingCurrencySelector=document.querySelector(".pricing__currency-selector"),basicPriceElement=document.getElementById("basic-price"),professionalPriceElement=document.getElementById("professional-price"),premiumPriceElement=document.getElementById("premium-price"),subcriptions=JSON.parse(localStorage.getItem("subcriptions"));async function fetchCurrency(){try{var e,r=await fetch("https://cdn.jsdelivr.net/npm/@fawazahmed0/currency-api@latest/v1/currencies/eur.json");if(r.ok)return{usd:(e=await r.json()).eur.usd,gbp:e.eur.gbp}}catch(e){return console.error("Error:",e),null}}function convertAmount(e,r){return{eur:"€"+Math.floor(e/r.usd),usd:"$"+e,gbp:"£"+Math.floor(e/r.usd*r.gbp)}}async function calculateCurrency(){var e;subcriptions||((e=await fetchCurrency())?(subcriptions={basic:convertAmount(0,e),professional:convertAmount(25,e),premium:convertAmount(60,e)},localStorage.setItem("subcriptions",JSON.stringify(subcriptions))):console.log("Failed to fetch currency rates."))}princingCurrencySelector.addEventListener("change",async e=>{let r=e.target.value;subcriptions||await calculateCurrency();e=e=>{switch(r){case"eur":return e.eur;case"usd":return e.usd;case"gbp":return e.gbp;default:return e.usd}};basicPriceElement.textContent=e(subcriptions.basic),professionalPriceElement.textContent=e(subcriptions.professional),premiumPriceElement.textContent=e(subcriptions.premium)}),calculateCurrency();